import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import {
  Wallet,
  ShoppingCart,
  CheckCircle2,
  Clock,
  TrendingUp,
  Plus,
  ArrowUpRight,
  AlertCircle,
  BarChart3,
  Star,
} from "lucide-react";
import { Link } from "wouter";
import type { Order } from "@shared/schema";
import { useAuth } from "@/hooks/useAuth";

interface UserAnalytics {
  totalOrders: number;
  totalSpent: string;
  ordersByStatus: {
    pending: number;
    processing: number;
    completed: number;
    failed: number;
  };
  popularServices: Array<{
    serviceId: string;
    serviceName: string;
    platform: string;
    orderCount: number;
  }>;
  spendingTrend: Array<{
    date: string;
    amount: number;
  }>;
}

export default function Dashboard() {
  const { user } = useAuth();

  // Fetch recent orders
  const { data: recentOrders, isLoading: ordersLoading } = useQuery<Order[]>({
    queryKey: ["/api/orders/recent"],
  });

  // Fetch user analytics
  const { data: analytics } = useQuery<UserAnalytics>({
    queryKey: ["/api/analytics/user"],
  });

  // Calculate stats
  const activeOrders = recentOrders?.filter(o => o.status === "pending" || o.status === "processing").length || 0;
  const completedToday = recentOrders?.filter(o => {
    const today = new Date().toDateString();
    const orderDate = new Date(o.createdAt).toDateString();
    return o.status === "completed" && orderDate === today;
  }).length || 0;
  const totalSpent = recentOrders?.reduce((sum, order) => sum + parseFloat(order.amount), 0) || 0;

  const stats = [
    {
      title: "Wallet Balance",
      value: `₹${parseFloat(user?.walletBalance || "0").toFixed(2)}`,
      icon: Wallet,
      iconColor: "text-green-500",
      bgColor: "bg-green-500/10",
      testId: "card-wallet-balance",
    },
    {
      title: "Active Orders",
      value: activeOrders.toString(),
      icon: Clock,
      iconColor: "text-yellow-500",
      bgColor: "bg-yellow-500/10",
      testId: "card-active-orders",
    },
    {
      title: "Completed Today",
      value: completedToday.toString(),
      icon: CheckCircle2,
      iconColor: "text-blue-500",
      bgColor: "bg-blue-500/10",
      testId: "card-completed-today",
    },
    {
      title: "Total Spent",
      value: `₹${totalSpent.toFixed(2)}`,
      icon: TrendingUp,
      iconColor: "text-purple-500",
      bgColor: "bg-purple-500/10",
      testId: "card-total-spent",
    },
  ];

  const getStatusBadge = (status: string) => {
    const variants: Record<string, { variant: "default" | "secondary" | "destructive" | "outline", label: string }> = {
      pending: { variant: "secondary", label: "Pending" },
      processing: { variant: "default", label: "Processing" },
      completed: { variant: "outline", label: "Completed" },
      failed: { variant: "destructive", label: "Failed" },
      refunded: { variant: "secondary", label: "Refunded" },
    };
    const config = variants[status] || variants.pending;
    return <Badge variant={config.variant}>{config.label}</Badge>;
  };

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between gap-4">
        <div>
          <h1 className="text-3xl font-semibold">Dashboard</h1>
          <p className="text-muted-foreground mt-1">
            Welcome back, {user?.firstName || "User"}
          </p>
        </div>
        <Button asChild data-testid="button-new-order">
          <Link href="/services">
            <Plus className="h-4 w-4 mr-2" />
            New Order
          </Link>
        </Button>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {stats.map((stat) => (
          <Card key={stat.title} data-testid={stat.testId}>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div className="space-y-2">
                  <p className="text-sm text-muted-foreground">{stat.title}</p>
                  <p className="text-2xl font-semibold">{stat.value}</p>
                </div>
                <div className={`w-12 h-12 rounded-md ${stat.bgColor} flex items-center justify-center`}>
                  <stat.icon className={`h-6 w-6 ${stat.iconColor}`} />
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Recent Activity */}
      <Card>
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-4">
          <CardTitle className="text-xl font-medium">Recent Orders</CardTitle>
          <Button variant="ghost" size="sm" asChild>
            <Link href="/orders">
              View All
              <ArrowUpRight className="h-4 w-4 ml-1" />
            </Link>
          </Button>
        </CardHeader>
        <CardContent>
          {ordersLoading ? (
            <div className="flex items-center justify-center py-8">
              <div className="animate-spin w-6 h-6 border-2 border-primary border-t-transparent rounded-full" />
            </div>
          ) : !recentOrders || recentOrders.length === 0 ? (
            <div className="text-center py-12 space-y-3">
              <ShoppingCart className="h-12 w-12 mx-auto text-muted-foreground/50" />
              <p className="text-muted-foreground">No orders yet</p>
              <Button asChild variant="outline" size="sm">
                <Link href="/services">Browse Services</Link>
              </Button>
            </div>
          ) : (
            <div className="space-y-4">
              {recentOrders.slice(0, 5).map((order) => (
                <div
                  key={order.id}
                  className="flex items-center justify-between p-4 rounded-md border hover-elevate"
                  data-testid={`order-item-${order.id}`}
                >
                  <div className="flex items-start gap-4 min-w-0 flex-1">
                    <div className="w-10 h-10 rounded-md bg-primary/10 flex items-center justify-center flex-shrink-0">
                      <ShoppingCart className="h-5 w-5 text-primary" />
                    </div>
                    <div className="min-w-0 flex-1">
                      <div className="font-medium truncate">Order #{order.id.slice(0, 8)}</div>
                      <div className="text-sm text-muted-foreground truncate">{order.targetLink}</div>
                      <div className="text-xs text-muted-foreground mt-1">
                        {new Date(order.createdAt).toLocaleDateString()} at {new Date(order.createdAt).toLocaleTimeString()}
                      </div>
                    </div>
                  </div>
                  <div className="flex items-center gap-4 flex-shrink-0">
                    <div className="text-right">
                      <div className="font-medium">₹{parseFloat(order.amount).toFixed(2)}</div>
                      <div className="text-sm text-muted-foreground">{order.quantity.toLocaleString()} units</div>
